import React from "react";
import ReactDOM from "react-dom";
//import { Fetch } from "react-request";
//import { Button } from "reactstrap";
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
import "./shards.min.css";

// import "assets/assets/scss/blk-design-system-react.scss";
// import "assets/assets/css/nucleo-icons.css";

class MyComponentClass extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      dropdownOpen: false,
      selectedLine: "None",
      selectLine: false,
      inOut: false,
      lineStop: "None"
    };
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  changeValue(num) {
    //ReactDOM.render(<MyComponentClassTwo />, document.getElementById("root"));
  }

  tester(a) {
      console.log(a)
  }

  render() {
    const people = ["A", "B", "C", "D", "E", "F", "G", "J", "K", "L", "M", "O", "P", "Q", "R", "S", "T", "V", "W", "X", "Z"];
     //var parseString = require('react-native-xml2js')
     fetch("http://webservices.nextbus.com/service/publicXMLFeed?command=routeList&a=unitrans")
       .then(response => response.text())
       .then(text => 
             this.tester(JSON.stringify(text))



            );
      if(this.state.inOut === false)
      {      
              return (
                      <div className="App">
                        <select className="custom-select">
                          <option>Choose your Bus Line</option>
                            {people.map((person, index) => {
                              return <option value={index} onChange={this.tester("1")}>{person}</option>;
                            })}    
                        </select>
                      </div>

              );

      }
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<MyComponentClass />, rootElement);


function getBusInfo() {
    return fetch('http://restbus.info/api/agencies/unitrans/routes/A')
        .then((response) => response.json())
        .then((responseJson) => {
            var x = responseJson.directions[0].shortTitle
            return x;
        })
        .catch((error) => {
            console.error(error);
        });
}

class FlavorForm extends React.Component {


  constructor(props) {
    super(props);
    this.state = {value: 'none',
                  test: JSON.parse('{ "name":"John", "age":30, "city":"New York"}'),
                  phase: '1'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }




    handleChange(event) {
    console.log(getBusInfo())
    this.setState({value: event.target.value, phase:'2', test: "Hello"});
  }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
  }


  getRouteInfo(line) {
      fetch('http://restbus.info/api/agencies/unitrans/routes/A')
          .then(response => response.json())
          .then(({jsonData}) => this.setState({test: jsonData}))
          .catch((error) => {
              // handle your errors here
              console.error(error)
          });
  }

  render() {
      const busLines = ["Select A Bus Line :)", "A - Downtown / 5th St / Alhambra", "B - Sycamore / Drake ", "C - Sycamore / Wake Forest", "D - Lake Blvd / Arlington", "E - Downtown / F St / J St", "F - Oak Ave / E. Alvarado / N. Anderson", "G - Anderson / Alvarado / N. Sycamore", "J - Anderson / Alvarado / N. Sycamore", "K - Lake Blvd / Arlington", "L", "M", "O", "P", "Q", "R", "S", "T", "V", "W", "X", "Z"];
      // fetch('http://restbus.info/api/agencies/unitrans/routes')
      // .then(response => response.json())
      //         .then((jsonData) => {
      //             // jsonData is parsed json object received from url
      //             console.log(jsonData)
      //         })
      //         .catch((error) => {
      //             // handle your errors here
      //             console.error(error)
      //         });

    if(this.state.phase == '1')
    {
      return (
          <form onSubmit={this.handleSubmit}>
            <label>
              Choose your Bus Line:
              <select class = "custom-select" value={this.state.value} onChange={this.handleChange}>
                    {busLines.map((bus, index) => {
                        return <option name={bus.substring(0,1)}>{bus}</option>;
                    })}    
              </select>
            </label>
          </form>
        );
    }
    if(this.state.phase == '2')
    {
        // fetch('http://restbus.info/api/agencies/unitrans/routes/A')
        // .then(response => response.json())
        //         .then((jsonData) => {
        //             // jsonData is parsed json object received from url
        //             console.log(jsonData);
        //         })
        //         .catch((error) => {
        //             // handle your errors here
        //             console.error(error)
        //         });




       return (
          <form onSubmit={this.handleSubmit}>
            <label>
              Direction: {this.state.test}

              <select class = "custom-select" onChange={this.handleChange}>

                    <option>InBound </option>
                    <option>OutBound</option>

              </select>
            </label>
          </form>
        ); 
    }
  }
}


