import React from "react";
import ReactDOM from "react-dom";
//import { Fetch } from "react-request";
//import { Button } from "reactstrap";
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
// import "assets/assets/scss/blk-design-system-react.scss";
// import "assets/assets/css/nucleo-icons.css";

class MyComponentClass extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      dropdownOpen: false,
      selectedLine: "None",
      selectLine: false,
      inOut: false,
      lineStop: "None"
    };
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  changeValue(num) {
    //ReactDOM.render(<MyComponentClassTwo />, document.getElementById("root"));
  }

  tester(a) {
      console.log(a)
  }

  render() {
    const people = ["A", "B", "C", "D", "E", "F", "G", "J", "K", "L", "M", "O", "P", "Q", "R", "S", "T", "V", "W", "X", "Z"];
     //var parseString = require('react-native-xml2js')
     fetch("http://webservices.nextbus.com/service/publicXMLFeed?command=routeList&a=unitrans")
       .then(response => response.text())
       .then(text => 
             this.tester(JSON.stringify(text))



            );
      if(this.state.inOut === false)
      {      
              return (
                      <div className="App">
                        <select className="browser-default custom-select">
                          <option>Choose your Bus Line</option>
                            {people.map((person, index) => {
                              return <option value={index} onChange={this.tester("1")}>{person}</option>;
                            })}    
                        </select>
                      </div>

              );

      }
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<MyComponentClass />, rootElement);


class FlavorForm extends React.Component {


  constructor(props) {
    super(props);
    this.state = {value: 'none',
                  phase: '1'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    const busLines = ["A", "B", "C", "D", "E", "F", "G", "J", "K", "L", "M", "O", "P", "Q", "R", "S", "T", "V", "W", "X", "Z"]; 
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Pick your favorite flavor:
          <select value={this.state.value} onChange={this.handleChange}>
                {busLines.map((bus, index) => {
                    return <option >{bus}</option>;
                })}    
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}


ReactDOM.render(<FlavorForm />, rootElement);
