function getBusInfo(param) {
    return fetch('http://restbus.info/api/agencies/unitrans/routes/' + param)
        .then((response) => response.json())
        .then((responseJson) => {
            var resultArray = [];
            console.log(responseJson.directions.length);
            for(var i = 0; i < responseJson.directions.length; i++) {
                resultArray.push(responseJson.directions[i].shortTitle)
            }

            return resultArray;
        })
        .catch((error) => {
            console.error(error);
        });
}



