import React from "react";
import ReactDOM from "react-dom";
//import { Fetch } from "react-request";
//import { Button } from "reactstrap";
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles.css";
import "./shards.min.css";

// import "assets/assets/scss/blk-design-system-react.scss";
// import "assets/assets/css/nucleo-icons.css";

class MyComponentClass extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      dropdownOpen: false,
      selectedLine: "None",
      selectLine: false,
      inOut: false,
      lineStop: "None"
    };
  }

  toggle() {
    this.setState(prevState => ({
      dropdownOpen: !prevState.dropdownOpen
    }));
  }

  changeValue(num) {
    //ReactDOM.render(<MyComponentClassTwo />, document.getElementById("root"));
  }

  tester(a) {
      console.log(a)
  }

  render() {
    const people = ["A", "B", "C", "D", "E", "F", "G", "J", "K", "L", "M", "O", "P", "Q", "R", "S", "T", "V", "W", "X", "Z"];
     //var parseString = require('react-native-xml2js')
     fetch("http://webservices.nextbus.com/service/publicXMLFeed?command=routeList&a=unitrans")
       .then(response => response.text())
       .then(text => 
             this.tester(JSON.stringify(text))



            );
      if(this.state.inOut === false)
      {      
              return (
                      <div className="App">
                        <select className="custom-select">
                          <option>Choose your Bus Line</option>
                            {people.map((person, index) => {
                              return <option value={index} onChange={this.tester("1")}>{person}</option>;
                            })}    
                        </select>
                      </div>

              );

      }
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<MyComponentClass />, rootElement);





function getBusInfo(param) {
    return fetch('http://restbus.info/api/agencies/unitrans/routes/' + param)
        .then((response) => response.json())
        .then((responseJson) => {
            var resultArray = [];
            console.log(responseJson.directions.length);
            for(var i = 0; i < responseJson.directions.length; i++) {
                resultArray.push(responseJson.directions[i].shortTitle)
            }

            return resultArray;
        })
        .catch((error) => {
            console.error(error);
        });
}



class FlavorForm extends React.Component {


  constructor(props) {
    super(props);
    this.state = {value: 'none',
                  test: JSON.parse('{ "name":"John", "age":30, "city":"New York"}'),
                  phase: '1',
                  stops: 'none',
                  direction: 'none'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }




    handleChange(event) {
      if(this.state.phase == '1') {
          var g = String(event.target.value).substring(0, 1);
          getBusInfo(g).then(result => {
              console.log(result.length)
              if(result.length == 0)
              {
                  console.log("ERROR");
              }
              if(result.length > 1) {
                  this.setState({phase: '2', test: result});
              }
              if(result.length == 1) {
                  this.setState({phase: '3', test: result});
              }

          })
      }
      if(this.state.phase == '2') {
          console.log(this.state.test)
          this.setState({phase: '3'});
      }
    //this.setState({value: event.target.value, phase:'2', test: getBusInfo('A')});
    }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
  }




